def shuffle_tada_data(data):
    shuffled_data = [data[0]]  # add first element to shuffled list
    remaining_data = data[1:-1]  # exclude first and last element
    random.shuffle(remaining_data)  # shuffle remaining elements

    # check if consecutive elements exist and swap if necessary
    for i in range(len(remaining_data) - 1):
        if abs(remaining_data[i][0] - remaining_data[i+1][0]) < 2.5:
            # if consecutive elements exist, swap them
            temp = remaining_data[i]
            remaining_data[i] = remaining_data[i+1]
            remaining_data[i+1] = temp

    shuffled_data.extend(remaining_data)  # add shuffled elements to shuffled list
    shuffled_data.append(data[-1])  # add last element to shuffled list

    return shuffled_data

